mapper.select:
select

  distinct

'true' as QUERYID,

from gh_call_system
where gh_call_system.c_phone1 like #{cB1,jdbcType=VARCHAR}
or gh_call_system.c_phone2 like #{cB1,jdbcType=VARCHAR}
or gh_call_system.c_phone3 like #{cB1,jdbcType=VARCHAR}
or gh_call_system.c_phone4 like #{cB1,jdbcType=VARCHAR}
